    <script>
        document.addEventListener('DOMContentLoaded', function () {
            const isAdmin = <?php echo $is_admin ? 'true' : 'false'; ?>;

            // Класс для управления модальным окном
            class ModalManager {
                constructor(modalId, closeButtonClass, openButtons) {
                    this.modal = document.getElementById(modalId);
                    this.closeButton = document.querySelector(closeButtonClass);
                    this.openButtons = document.querySelectorAll(openButtons);

                    this.init();
                }

                init() {
                    // Закрытие модального окна по кнопке
                    this.closeButton.addEventListener('click', () => this.closeModal());

                    // Открытие модального окна по нажатию на кнопки
                    this.openButtons.forEach(button => {
                        button.addEventListener('click', () => this.openModal());
                    });
                }

                openModal() {
                    this.modal.style.display = 'block';
                }

                closeModal() {
                    this.modal.style.display = 'none';
                }
            }

            // if (!isAdmin) {
            //     setTimeout(function () {
            //         var modal = document.getElementById('transactionModal');
            //         modal.style.display = 'block';
            //     }, 3000);
            // }

            // Инициализация ModalManager для модального окна с id 'transactionModal'
            const modalManager = new ModalManager(
                'transactionModal', // ID модального окна
                '.custom-close-button', // Класс кнопки закрытия
                '.open-modal-button' // Класс кнопок открытия
            );

            // Если пользователь admin, показываем скрытый элемент
            if (isAdmin) {
                document.querySelector('[data-hidden="listPayment"]').style.display = 'block';
            }
        });
