    const bonusPercents = [0, 50, 75, 100]; // ÐŸÑ€Ð¾Ñ†ÐµÐ½Ñ‚Ñ‹ Ð±Ð¾Ð½ÑƒÑÐ° Ð´Ð»Ñ ÐºÐ°Ð¶Ð´Ð¾Ð¹ ÐºÐ½Ð¾Ð¿ÐºÐ¸

