    async function getCryptoRate(cryptoId, network = null) {
      try {
        const response = await fetch(`https://api.coingecko.com/api/v3/simple/price?ids=${cryptoId}&vs_currencies=usd`);
        const data = await response.json();

        if (data && data[cryptoId] && data[cryptoId].usd) {
          return {
            rate: data[cryptoId].usd,
            name: cryptoId === 'tether'
              ? (network === 'trc20' ? 'USDT (TRC20)' : 'USDT (BEP20)')
              : cryptoId === 'bitcoin' ? 'Bitcoin (BTC)'
                : cryptoId === 'ethereum' ? 'Ethereum (ETH)'
                  : cryptoId === 'trx' ? 'TRON (TRX)'
                    : cryptoId === 'bnb' ? 'BNB (BNB)'
                      : cryptoId === 'ton' ? 'TON (TON)'
                        : cryptoId.charAt(0).toUpperCase() + cryptoId.slice(1)
          };
        }
        return null;
      } catch (error) {
        console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ð¸ ÐºÑƒÑ€ÑÐ° ÐºÑ€Ð¸Ð¿Ñ‚Ð¾Ð²Ð°Ð»ÑŽÑ‚Ñ‹:', error);
        return null;
      }
    }

