    // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿Ð¾ÐºÐ°Ð·Ð° Ð¼Ð¾Ð´Ð°Ð»ÑŒÐ½Ð¾Ð³Ð¾ Ð¾ÐºÐ½Ð°
    function showStatusModal() {
      const statusModal = document.getElementById('statusModal');
      if (!statusModal) return;

      // ÐŸÐ¾ÐºÐ°Ð·Ñ‹Ð²Ð°ÐµÐ¼ Ð¼Ð¾Ð´Ð°Ð»ÑŒÐ½Ð¾Ðµ Ð¾ÐºÐ½Ð¾
      statusModal.style.display = 'flex';

      // ÐÐ²Ñ‚Ð¾Ð¼Ð°Ñ‚Ð¸Ñ‡ÐµÑÐºÐ¾Ðµ Ð·Ð°ÐºÑ€Ñ‹Ñ‚Ð¸Ðµ Ñ‡ÐµÑ€ÐµÐ· 5 ÑÐµÐºÑƒÐ½Ð´
      const autoCloseTimeout = setTimeout(() => {
        closeStatusModal();
      }, 5000);

      // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð·Ð°ÐºÑ€Ñ‹Ñ‚Ð¸Ñ
      function closeStatusModal() {
        statusModal.style.display = 'none';
        clearTimeout(autoCloseTimeout);
        document.removeEventListener('keydown', handleEscape);
        statusModal.removeEventListener('click', handleOutsideClick);
      }

      // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚Ñ‡Ð¸Ðº Esc
      function handleEscape(e) {
        if (e.key === 'Escape') {
          closeStatusModal();
        }
      }

      // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚Ñ‡Ð¸Ðº ÐºÐ»Ð¸ÐºÐ° Ð²Ð½Ðµ Ð¼Ð¾Ð´Ð°Ð»ÑŒÐ½Ð¾Ð³Ð¾ Ð¾ÐºÐ½Ð°
      function handleOutsideClick(e) {
        if (e.target === statusModal) {
          closeStatusModal();
        }
      }

      // Ð—Ð°ÐºÑ€Ñ‹Ñ‚Ð¸Ðµ Ð¿Ð¾ ÐºÐ½Ð¾Ð¿ÐºÐµ
      const closeButton = statusModal.querySelector('[data-cy="close-modal-button"]');
      if (closeButton) {
        closeButton.addEventListener('click', closeStatusModal);
      }

      // Ð”Ð¾Ð±Ð°Ð²Ð»ÑÐµÐ¼ Ð¾Ð±Ñ€Ð°Ð±Ð¾Ñ‚Ñ‡Ð¸ÐºÐ¸
      document.addEventListener('keydown', handleEscape);
      statusModal.addEventListener('click', handleOutsideClick);
    }

