    // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð·Ð°Ð¿ÑƒÑÐºÐ° Ñ‚Ð°Ð¹Ð¼ÐµÑ€Ð° Ð¾Ð±Ñ€Ð°Ñ‚Ð½Ð¾Ð³Ð¾ Ð¾Ñ‚ÑÑ‡ÐµÑ‚Ð°
    function startCountdown() {
      // Ð£ÑÑ‚Ð°Ð½Ð°Ð²Ð»Ð¸Ð²Ð°ÐµÐ¼ Ð²Ñ€ÐµÐ¼Ñ Ð¾ÐºÐ¾Ð½Ñ‡Ð°Ð½Ð¸Ñ (Ñ‚ÐµÐºÑƒÑ‰ÐµÐµ Ð²Ñ€ÐµÐ¼Ñ + 2 Ñ‡Ð°ÑÐ°)
      const now = new Date();
      const expirationTime = new Date(now.getTime() + 2 * 60 * 60 * 1000); // Ð”Ð¾Ð±Ð°Ð²Ð»ÑÐµÐ¼ 2 Ñ‡Ð°ÑÐ°

      // Ð¤Ð¾Ñ€Ð¼Ð°Ñ‚Ð¸Ñ€ÑƒÐµÐ¼ Ð´Ð°Ñ‚Ñƒ Ð¸ Ð²Ñ€ÐµÐ¼Ñ Ð´Ð»Ñ Ð¾Ñ‚Ð¾Ð±Ñ€Ð°Ð¶ÐµÐ½Ð¸Ñ
      const options = {
        day: "2-digit",
        month: "short",
        year: "numeric",
        hour: "2-digit",
        minute: "2-digit",
      };
      const expirationDateTime =
        expirationTime.toLocaleDateString("es-ES", options) + " hrs.";

      // Ð£ÑÑ‚Ð°Ð½Ð°Ð²Ð»Ð¸Ð²Ð°ÐµÐ¼ Ð´Ð°Ñ‚Ñƒ Ð¸ Ð²Ñ€ÐµÐ¼Ñ Ð² Ð±Ð»Ð¾ÐºÐµ
      const expirationElement = document.querySelector(
        '[data-bind="text:expirationDateTime"]'
      );
      if (expirationElement) {
        expirationElement.textContent = expirationDateTime;
      }

      // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ Ð¾Ð±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ñ Ñ‚Ð°Ð¹Ð¼ÐµÑ€Ð°
      function updateTimer() {
        const now = new Date();
        const diff = expirationTime - now;

        // Ð•ÑÐ»Ð¸ Ð²Ñ€ÐµÐ¼Ñ Ð¸ÑÑ‚ÐµÐºÐ»Ð¾
        if (diff <= 0) {
          clearInterval(timerInterval);
          document.getElementById("timer_hor_1").textContent = "00";
          document.getElementById("timer_min_1").textContent = "00";
          document.getElementById("timer_seg_1").textContent = "00";
          return;
        }

        // Ð Ð°ÑÑÑ‡Ð¸Ñ‚Ñ‹Ð²Ð°ÐµÐ¼ Ð¾ÑÑ‚Ð°Ð²ÑˆÐµÐµÑÑ Ð²Ñ€ÐµÐ¼Ñ
        const hours = Math.floor(diff / (1000 * 60 * 60));
        const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
        const seconds = Math.floor((diff % (1000 * 60)) / 1000);

        // ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ Ð¾Ñ‚Ð¾Ð±Ñ€Ð°Ð¶ÐµÐ½Ð¸Ðµ Ñ‚Ð°Ð¹Ð¼ÐµÑ€Ð°
        document.getElementById("timer_hor_1").textContent = hours
          .toString()
          .padStart(2, "0");
        document.getElementById("timer_min_1").textContent = minutes
          .toString()
          .padStart(2, "0");
        document.getElementById("timer_seg_1").textContent = seconds
          .toString()
          .padStart(2, "0");
      }

      // Ð—Ð°Ð¿ÑƒÑÐºÐ°ÐµÐ¼ Ñ‚Ð°Ð¹Ð¼ÐµÑ€ Ð¸ Ð¾Ð±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ ÑÑ€Ð°Ð·Ñƒ
      updateTimer();
      const timerInterval = setInterval(updateTimer, 1000);
    }

