    <script>
      document.addEventListener("DOMContentLoaded", function () {
        // Находим все вкладки (и мобильные и десктопные)
        const tabs = document.querySelectorAll("._tab_1tilm_1");
        const tabContents = document.querySelectorAll("._tab-content");
        const titleSpan = document.querySelector("._text_1konr_10 span");
        const mobileBreakpoint = 1025;

        // Функция для получения параметра из URL
        function getUrlParameter(name) {
          name = name.replace(/[\[\]]/g, '\\$&');
          const regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)');
          const results = regex.exec(window.location.href);
          if (!results) return null;
          if (!results[2]) return '';
          return decodeURIComponent(results[2].replace(/\+/g, ' '));
        }

        function isMobile() {
          return window.innerWidth <= mobileBreakpoint;
        }

        // Функция для активации вкладки по тексту (работает с обеими версиями)
        function activateTabByText(tabText) {
          // Находим все вкладки с нужным текстом
          const targetTabs = Array.from(tabs).filter(tab =>
            tab.textContent.trim().toLowerCase() === tabText.toLowerCase()
          );

          if (targetTabs.length > 0) {
            // Деактивируем все вкладки
            tabs.forEach(tab => tab.classList.remove("_tab_active_1tilm_30"));

            // Активируем найденные вкладки
            targetTabs.forEach(tab => tab.classList.add("_tab_active_1tilm_30"));

            return targetTabs[0];
          }
          return null;
        }

        // Функция для загрузки данных транзакций (depósitos)
        async function loadDeposits() {
          try {
            const response = await fetch('detalization_db.php', {
              headers: {
                'X-Requested-With': 'XMLHttpRequest'
              }
            });

            if (!response.ok) throw new Error('Network response was not ok');

            const data = await response.json();

            if (data.success && data.transactions && data.transactions.length > 0) {
              renderTransactions(data.transactions, 'deposits');
              return true;
            }
            return false;
          } catch (error) {
            console.error('Error loading deposits:', error);
            return false;
          }
        }

        // Функция для загрузки данных платежей (pagos)
        async function loadPagos() {
          try {
            const response = await fetch('detalization_pagos_db.php', {
              headers: {
                'X-Requested-With': 'XMLHttpRequest'
              }
            });

            if (!response.ok) throw new Error('Network response was not ok');

            const data = await response.json();

            if (data.success && data.transactions && data.transactions.length > 0) {
              renderTransactions(data.transactions, 'pagos');
              return true;
            }
            return false;
          } catch (error) {
            console.error('Error loading pagos:', error);
            return false;
          }
        }

        // Функция отрисовки транзакций
        function renderTransactions(transactions, type) {
          // Определяем контейнер в зависимости от типа
          let container;
          if (type === 'deposits') {
            container = document.querySelector("._container_10mk7_1._empty_ljrp9_12._tab-content._active-tab");
          } else if (type === 'pagos') {
            container = document.querySelector("._empty_ljrp9_1._tab-content");
          }

          if (!container) return;

          if (isMobile()) {
            // Мобильная версия
            let transactionsHTML = `<div class="_container_d89i_1 _tab-content _active-tab" id="transactions-${type}-container">`;

            transactions.forEach(transaction => {
              const [date, time] = formatTransactionDate(transaction.transacciones_data);
              const statusClass = getStatusClass(transaction.estado);

              transactionsHTML += `
                    <div class="_transaction_19nvg_1">
                        <div class="_upper_19nvg_22">
                            <div class="_upperLeft_19nvg_28">
                                <div class="_mobileSum_19nvg_28">
                                    <div class="_mobileAmount_19nvg_28">${transaction.transacciones_monto} <?php echo htmlspecialchars($currency); ?></div>
                                    <div class="_mobileId_19nvg_34">
                                        <div>Transacción</div>
                                        <div>${transaction.transacción_number}</div>
                                    </div>
                                </div>
                            </div>
                            <div class="_upperRight_19nvg_49">
                                <div class="_mobileDate_19nvg_55">
                                    <div class="_mobileDate_date_19nvg_61">${date}</div>
                                    <div class="_mobileDate_time_19nvg_68">${time}</div>
                                </div>
                            </div>
                        </div>
                        <div class="_divide_19nvg_43"></div>
                        <div class="_lower_19nvg_75">
                            <div>
                                <div class="_mobileMethod_heading_19nvg_81">Método de pago</div>
                                <div class="_mobileMethod_method_19nvg_90">${transaction.método_de_pago}</div>
                            </div>
                            <div>
                                <div class="${statusClass}">
                                    <p class="_status_19nvg_16">${transaction.estado}</p>
                                </div>
                            </div>
                        </div>
                    </div>
                `;
            });

            transactionsHTML += `</div>`;
            container.outerHTML = transactionsHTML;
          } else {
            // Десктопная версия
            let transactionsHTML = `
                <div class="_container_10mk7_1 _tab-content _active-tab" id="transactions-${type}-container">
                    <div class="_headers_10mk7_18">
                        <div class="_item_10mk7_35">Fecha y Hora</div>
                        <div class="_item_10mk7_35">Transacción</div>
                        <div class="_item_10mk7_35">Monto</div>
                        <div class="_item_10mk7_35">Método de pago</div>
                        <div class="_item_10mk7_35">Estado</div>
                    </div>
            `;

            transactions.forEach(transaction => {
              const [date, time] = formatTransactionDate(transaction.transacciones_data);
              const statusClass = getStatusClass(transaction.estado);

              transactionsHTML += `
                    <div class="_transaction_19nvg_1 _transaction_desktop_19nvg_141">
                        <div class="_date_19nvg_96">${date} <span class="_time_19nvg_99">${time}</span></div>
                        <div>${transaction.transacción_number}</div>
                        <div class="_sum_19nvg_105">${transaction.transacciones_monto} <?php echo htmlspecialchars($currency); ?></div>
                        <div>${transaction.método_de_pago}</div>
                        <div class="${statusClass}">
                            <p class="_status_19nvg_16">${transaction.estado}</p>
                        </div>
                    </div>
                `;
            });

            transactionsHTML += `</div>`;
            container.outerHTML = transactionsHTML;
          }
        }

        // Форматирование даты транзакции
        function formatTransactionDate(dateString) {
          if (!dateString || dateString === 'N/A') {
            return ['N/A', 'N/A'];
          }
          
          // Если дата уже отформатирована (dd.mm.yyyy hh:mm)
          if (dateString.includes('.') && dateString.includes(':')) {
            const parts = dateString.split(' ');
            return [parts[0] || 'N/A', parts[1] || 'N/A'];
          }
          
          // Парсим дату из базы данных
          const dateObj = new Date(dateString);
          if (isNaN(dateObj.getTime())) {
            return ['N/A', 'N/A'];
          }
          
          const date = `${dateObj.getDate().toString().padStart(2, '0')}.${(dateObj.getMonth() + 1).toString().padStart(2, '0')}.${dateObj.getFullYear()}`;
          const time = `${dateObj.getHours().toString().padStart(2, '0')}:${dateObj.getMinutes().toString().padStart(2, '0')}`;
          return [date, time];
        }


        function getStatusClass(status) {
          const statusLower = status.toLowerCase();
          if (statusLower === 'declined') return '_declined_19nvg_110';
          if (statusLower === 'esperando') return '_waiting_19nvg_111';
          if (statusLower === 'completed') return '_completed_19nvg_109';
          return '';
        }

        async function switchContent(activeTabText) {
          const isDepositosTab = activeTabText.toLowerCase() === "depósitos";
          const isPagosTab = activeTabText.toLowerCase() === "pagos";

          // Находим все контентные блоки
          const depositsContainer = document.getElementById("transactions-deposits-container");
          const pagosContainer = document.getElementById("transactions-pagos-container");
          const emptyDepositsContainer = document.querySelector("._container_10mk7_1._empty_ljrp9_12._tab-content._active-tab");
          const emptyPagosContainer = document.querySelector("._empty_ljrp9_1._tab-content");

          // Сначала скрываем все
          if (depositsContainer) depositsContainer.style.display = "none";
          if (pagosContainer) pagosContainer.style.display = "none";
          if (emptyDepositsContainer) emptyDepositsContainer.style.display = "none";
          if (emptyPagosContainer) emptyPagosContainer.style.display = "none";

          // Показываем нужный контент
          if (isDepositosTab) {
            if (depositsContainer) {
              depositsContainer.style.display = "flex";
            } else if (emptyDepositsContainer) {
              emptyDepositsContainer.style.display = "flex";
            }

            // Загружаем данные, если контейнер пустой
            if (!depositsContainer) {
              await loadDeposits();
            }
          } else if (isPagosTab) {
            if (pagosContainer) {
              pagosContainer.style.display = "flex";
            } else if (emptyPagosContainer) {
              emptyPagosContainer.style.display = "flex";
            }

            // Загружаем данные, если контейнер пустой
            if (!pagosContainer) {
              await loadPagos();
            }
          }
        }

        function updateTitleText(tabText) {
          if (titleSpan) {
            titleSpan.textContent = tabText.toLowerCase();
          }
        }

        // Инициализация
        async function init() {
          // Проверяем параметр activeTab из URL
          const activeTabParam = getUrlParameter('activeTab');

          // Если есть параметр activeTab=pagos, активируем вкладку pagos
          if (activeTabParam && activeTabParam.toLowerCase() === 'pagos') {
            const pagosTab = activateTabByText('pagos');
            if (pagosTab) {
              const tabText = pagosTab.textContent.trim();
              await switchContent(tabText);
              updateTitleText(tabText);
              return;
            }
          }

          // Иначе активируем дефолтную вкладку (depósitos)
          const defaultActiveTab = document.querySelector("._tab_1tilm_1._tab_active_1tilm_30");
          if (defaultActiveTab) {
            const tabText = defaultActiveTab.textContent.trim();
            await switchContent(tabText);
            updateTitleText(tabText);
          }
        }

        // Обработчики кликов на вкладки
        tabs.forEach((tab) => {
          tab.addEventListener("click", async function () {
            if (this.classList.contains("_tab_active_1tilm_30")) return;

            // Активируем все вкладки с таким же текстом
            const tabText = this.textContent.trim();
            activateTabByText(tabText);

            await switchContent(tabText);
            updateTitleText(tabText);
          });
        });

        // Обработчик изменения размера окна
        window.addEventListener("resize", function () {
          const activeTab = document.querySelector("._tab_1tilm_1._tab_active_1tilm_30");
          if (activeTab) {
            const tabText = activeTab.textContent.trim();
            switchContent(tabText);

            // Перерисовываем транзакции при изменении размера, если нужно
            const depositsContainer = document.getElementById("transactions-deposits-container");
            const pagosContainer = document.getElementById("transactions-pagos-container");

            if (tabText.toLowerCase() === "depósitos" && depositsContainer) {
              const transactions = Array.from(depositsContainer.querySelectorAll("._transaction_19nvg_1"))
                .map(el => ({}));
              if (transactions.length > 0) {
                renderTransactions(transactions, 'deposits');
              }
            } else if (tabText.toLowerCase() === "pagos" && pagosContainer) {
              const transactions = Array.from(pagosContainer.querySelectorAll("._transaction_19nvg_1"))
                .map(el => ({}));
              if (transactions.length > 0) {
                renderTransactions(transactions, 'pagos');
              }
            }
          }
        });

        // Запускаем инициализацию
        init();
      });
    </script>
