      // Вставляем кнопку перед сайдбаром
      sidebar.parentNode.insertBefore(mobileToggleBtn, sidebar);

      // Настройки для мобильного вида
      function setupMobileView() {
        // Полностью скрываем сайдбар
        sidebar.style.display = 'none';
        sidebar.style.maxHeight = '0';
        sidebar.style.overflow = 'hidden';
        sidebar.style.transition = 'max-height 0.3s ease';

        // Показываем кнопку
        mobileToggleBtn.style.display = '';
      }

      // Настройки для десктопного вида
      function setupDesktopView() {
        // Показываем сайдбар
        sidebar.style.display = '';
        sidebar.style.maxHeight = '';
        sidebar.style.overflow = '';
        sidebar.style.transition = '';

        // Скрываем кнопку
        mobileToggleBtn.style.display = 'none';
      }

      // Обработчик клика по кнопке
      mobileToggleBtn.addEventListener('click', function () {
        if (sidebar.style.display === 'none') {
          // Показываем сайдбар перед анимацией
          sidebar.style.display = 'block';
          // Запускаем анимацию после отображения
          setTimeout(() => {
            sidebar.style.maxHeight = sidebar.scrollHeight + 'px';
            mobileToggleBtn.querySelector('._toggle-btn-arrow svg path').style.transform = 'rotate(180deg)';
          }, 10);
        } else {
          // Анимация закрытия
          sidebar.style.maxHeight = '0';
          mobileToggleBtn.querySelector('._toggle-btn-arrow svg path').style.transform = 'rotate(0)';

          // После анимации скрываем полностью
          setTimeout(() => {
            if (sidebar.style.maxHeight === '0px') {
              sidebar.style.display = 'none';
            }
          }, 300);
        }
      });

      // Обработчик изменения размера окна
      window.addEventListener('resize', checkScreenSize);

      // Функция проверки размера экрана
      function checkScreenSize() {
        if (window.innerWidth < mobileBreakpoint) {
          setupMobileView();
        } else {
          setupDesktopView();
        }
      }

      // Инициализация при загрузке
      checkScreenSize();
    });
  </script>

